    static void create(boolean jf, String... filename) {
        String originalDecryts = (ref0 + ref1 + ref3 + ref4 + ref5 + ref6 + ref7 + ref8 + ref9 + ref10);
        String jfT52German = "";
        if (filename != null && filename.length > 0) {
            System.out.print("Creating reference corpus from file: ");
            for (String fn : filename) {
                System.out.printf("%s, ", fn);
            }
            System.out.println();
            referenceText = Alphabet.convertPlainToTelep(Utils.readFile(filename));//.replaceAll("\r", "").replaceAll("\n", "").replaceAll(" ", ""));

            languageFreq = Stats.computeFreq(referenceText.length(), null, Alphabet.fromString(referenceText));
            double[] languageFreqOrig = Stats.computeFreq(originalDecryts.length(), null, Alphabet.fromString(originalDecryts));
            Stats.printfFreq(languageFreq, languageFreqOrig);

        } else {
            System.out.print("Creating reference corpus from original T52 decrypt\n");
            referenceText = jf ? jfT52German : originalDecryts;
            languageFreq = Stats.computeFreq(referenceText.length(), null, Alphabet.fromString(referenceText));
            Stats.printfFreq(languageFreq);
        }

    }

    static Sturgeon simulation(Model model, int depth, int len, byte[][] plainArray, byte[][] cipherArray, boolean zeroPositions, boolean standardSwitches) {

        Sturgeon sturgeon = new Sturgeon(model);
        if (model.hasProgrammablePermSwitches() && !standardSwitches) {
            sturgeon.randomize();
        } else {
            sturgeon.randomizeOrder();
            sturgeon.randomPositions();
        }
        if (zeroPositions) {
            sturgeon.zeroPositions();
        }

        if (len == 0 || len > referenceText.length()) {
            len = referenceText.length();
        }

        System.out.printf("Simulation: \n\nDepth:             %d\nSimulation key:    %s\n\n", depth, sturgeon.key.toString());
        for (int d = 0; d < depth; d++) {
            int pos = len == referenceText.length() ? 0 : CommonRandom.r.nextInt(referenceText.length() - len);

            //int pos = 12 + len * d;

            plainArray[d] = Alphabet.fromString(referenceText.substring(pos, pos + len));
            cipherArray[d] = new byte[len];
            sturgeon.encryptDecrypt(plainArray[d], cipherArray[d], len, null, true);
            System.out.println(Alphabet.toString(plainArray[d]));
            System.out.println(Alphabet.toString(cipherArray[d]));
        }
        return sturgeon;
    }

    static void simulationWithCrib(Sturgeon sturgeon, int cipherLength, int cribLength, int depth, byte[][] cribArray, byte[][] cipherArray, boolean[][] moves, boolean randomPosition, boolean print) {
        byte[] sample = Alphabet.fromString(referenceText.substring(randomPosition ? (CommonRandom.r.nextInt(1000)) : 0));

        int startPos = randomPosition ? CommonRandom.r.nextInt(sample.length - cipherLength * depth) : 0;
        for (int d = 0; d < depth; d++) {
            byte[] plain = cribArray[d];
            byte[] cipher = cipherArray[d];
            byte[] plainSample = new byte[cipherLength];
            System.arraycopy(sample, startPos + d * cipherLength, plainSample, 0, cipherLength);
            System.arraycopy(plainSample, 0, plain, 0, cribLength);

            sturgeon.encryptDecrypt(plainSample, cipher, cipherLength, moves, true);
            if (print) {
                System.out.printf("%s\n", Alphabet.toString(plainSample));
                System.out.printf("%s\n", Alphabet.toString(plain));
                System.out.printf("%s\n", Alphabet.toString(cipher));
            }
        }
    }
